mapper.select:
SELECT b.bno, b.title, b.reg_date regdate, 
	CAST((COUNT(fno) / (SELECT COUNT(username) FROM student WHERE cno = #{no}) *100) AS signed integer) rate
FROM filebox b
LEFT JOIN file f ON b.bno = f.file_box_bno
WHERE cno = #{no}
AND tag = #{tag}

GROUP BY b.bno
ORDER BY b.bno DESC
LIMIT #{limit}, #{amount}

SELECT count(bno)
FROM filebox b
WHERE cno = #{no}
AND tag = #{tag}

mapper.update:
UPDATE filebox
SET title = #{title},
	content = #{content}
WHERE bno = #{bno}
AND tag = #{tag}

mapper.select:
SELECT tag
FROM filebox
WHERE bno = #{bno}
